import React, { useRef, useState, useEffect } from "react";
import axios from "axios";
import TaskForm from './components/TaskForm.js'
import { useAuth } from "react-oidc-context";
import {
  AppBar,
  Toolbar,
  Typography,
  Button,
  Container,
  TextField,
  Grid,
  Card,
  CardContent,
  CardActions,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
  Snackbar,
} from "@mui/material";

// Helper function to check if task deadline is within 2 days
const isDeadlineApproaching = (deadline) => {
  const now = new Date();
  const taskDeadline = new Date(deadline);
  const timeDiff = taskDeadline - now;
  const twoDaysInMillis = 48 * 60 * 60 * 1000; // 48 hours
  return timeDiff <= twoDaysInMillis && timeDiff >= 0;
};

function App() {
  const auth = useAuth();
  const [tasks, setTasks] = useState([]);
  const [users, setUsers] = useState([]);
  const [userRole, setUserRole] = useState('');
  const [newTask, setNewTask] = useState({
    title: "",
    description: "",
    assignee: "",
    deadline: "",
    status: "PENDING"
  });
  const [error, setError] = useState("");
  const [teamMembers, setTeamMembers] = useState([]);
  const [notification, setNotification] = useState(null);

  const API_URLS = {
    fetchTask: "https://71aos5nio2.execute-api.eu-west-1.amazonaws.com/getTask",
    createTask: "https://plaaw9qjn7.execute-api.eu-west-1.amazonaws.com/createTask",
    updateTask: "https://rlyzlu0dl0.execute-api.eu-west-1.amazonaws.com/updateTask",
    deleteTask: "https://1b4916boze.execute-api.eu-west-1.amazonaws.com/delete",
    fetchUsers: "https://your-api-url/getUsers"
  };

  useEffect(() => {
    if (auth.isAuthenticated) {
      fetchUserRole();
      fetchTasks();
      if (isAdmin()) {
        fetchUsers();
      }
    }
  }, [auth.isAuthenticated]);

  const fetchUserRole = () => {
    const username = auth.user?.profile["cognito:username"];
    if (username === "admin") {
      setUserRole("ADMIN");
    } else {
      setUserRole("MEMBER");
    }
  };

  const isAdmin = () => userRole === 'ADMIN';

  const fetchTasks = async () => {
    try {
      const response = await fetch(API_URLS.fetchTask);
      const data = await response.json();

      if (!isAdmin()) {
        const userEmail = auth.user?.profile.email;
        const filteredTasks = data.filter(task => task.assignee === userEmail);
        setTasks(filteredTasks);
      } else {
        setTasks(data);
      }

      // Check for tasks with deadlines approaching
      data.forEach(task => {
        if (isDeadlineApproaching(task.deadline)) {
          setNotification({ 
            message: `Task ${task.title} deadline is approaching!`, 
            severity: 'warning' 
          });
        }
      });

    } catch (err) {
      setError(err.message || "Failed to fetch tasks");
    }
  };

  // Create task handler
  const handleCreateTask = async () => {
    if (!isAdmin()) return;

    const newTask = {
      title: titleRef.current.value,
      description: descriptionRef.current.value,
      assignee: assigneeRef.current.value,
      deadline: deadlineRef.current.value,
      status: "PENDING",
    };

    try {
      const response = await axios.post(API_URLS.createTask, newTask, {
        headers: {
          "Content-Type": "application/json",
        },
      });
      setError("");
      fetchTasks();
    } catch (err) {
      setError("Failed to create task");
    }
  };

  const handleDeleteTask = async (taskId) => {
    if (!isAdmin()) return;

    try {
      await axios.delete(API_URLS.deleteTask, {
        data: { TaskID: taskId },
        headers: { "Content-Type": "application/json" },
      });
      setTasks((prevTasks) => prevTasks.filter((task) => task.TaskID !== taskId));
    } catch (err) {
      setError("Failed to delete task");
    }
  };

  const handleTaskStatusUpdate = (taskId, newStatus) => {
    if (!isAdmin()) return;

    axios.put(API_URLS.updateTask, { status: newStatus, id: taskId }, {
      headers: { "Content-Type": "application/json" }
    }).then((response) => {
      setTasks((prevTasks) =>
        prevTasks.map((task) => task.TaskID === taskId ? { ...task, ...response.data } : task)
      );
    }).catch(() => setError("Failed to update task"));
  };

  const TaskList = () => (
    <Grid item xs={12} md={isAdmin() ? 6 : 12} container spacing={2}>
      <Typography variant="h5" gutterBottom>
        {isAdmin() ? "All Tasks" : "My Tasks"}
      </Typography>
      {tasks.length === 0 ? <Typography>No tasks available</Typography> :
        tasks.map((task) => (
          <Card 
            key={task.TaskID}
            sx={{ mb: 2, border: isDeadlineApproaching(task.deadline) ? '2px solid red' : 'none' }}
          >
            <CardContent>
              <Typography variant="h6">{task.title}</Typography>
              <Typography>{task.description}</Typography>
              <Typography>Status: {task.status}</Typography>
              <Typography>Deadline: {task.deadline}</Typography>
            </CardContent>
            {isAdmin() && (
              <CardActions>
                <Button variant="contained" color="success" onClick={() => handleTaskStatusUpdate(task.TaskID, "COMPLETED")}>Mark as Completed</Button>
                <Button variant="contained" color="warning" onClick={() => handleTaskStatusUpdate(task.TaskID, "IN_PROGRESS")}>Mark as In Progress</Button>
                <Button variant="contained" color="error" onClick={() => handleDeleteTask(task.TaskID)}>Delete Task</Button>
              </CardActions>
            )}
          </Card>
        ))}
    </Grid>
  );

  return (
    <div>
      <AppBar position="static">
        <Toolbar>
          <Typography variant="h6" sx={{ flexGrow: 1 }}>
            Task Management App ({userRole})
          </Typography>
          <Button color="inherit" onClick={() => auth.removeUser()}>Sign Out</Button>
        </Toolbar>
      </AppBar>
      <Container sx={{ mt: 4 }}>
        <Typography variant="h4" gutterBottom>Welcome, {auth.user?.profile["cognito:username"]}</Typography>
        <Grid container spacing={2}>
          {isAdmin() && <AdminTaskCreation />}
          <TaskList />
        </Grid>
        {error && (
          <Typography color="error" sx={{ mt: 2 }}>{error}</Typography>
        )}
        {notification && (
          <Snackbar
            open={true}
            message={notification.message}
            severity={notification.severity}
          />
        )}
      </Container>
    </div>
  );
}

export default App;
