import React, { useState, useEffect } from 'react';

const TaskCreationForm = ({ isOpen, onClose }) => {
    const [formData, setFormData] = useState({
        title: '',
        description: '',
        startDate: '',
        dueDate: '',
        assignedTo: []
    });

    const [errors, setErrors] = useState({});
    const [teamMembers, setTeamMembers] = useState([]);
    const [loading, setLoading] = useState(true);
    const [fetchError, setFetchError] = useState(null);

    // Fetch team members from the API
    const fetchTeamMembers = async () => {
        try {
            const response = await fetch('https://pwe7hvogc6.execute-api.eu-west-1.amazonaws.com/users');
            if (!response.ok) {
                throw new Error('Failed to fetch team members');
            }
            const data = await response.json();
            const members = data.filter((user) => user.role === 'member');
            setTeamMembers(members);
        } catch (error) {
            setFetchError(error.message);
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchTeamMembers();
    }, []);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: value
        }));

        // Clear error when user starts typing
        if (errors[name]) {
            setErrors(prev => ({
                ...prev,
                [name]: ''
            }));
        }
    };

    const handleCheckboxChange = (id) => {
        setFormData(prev => ({
            ...prev,
            assignedTo: prev.assignedTo.includes(id)
                ? prev.assignedTo.filter(memberId => memberId !== id)
                : [...prev.assignedTo, id]
        }));

        if (errors.assignedTo) {
            setErrors(prev => ({
                ...prev,
                assignedTo: ''
            }));
        }
    };

    const validateForm = () => {
        const newErrors = {};

        if (!formData.title.trim()) {
            newErrors.title = 'Title is required';
        }

        if (!formData.description.trim()) {
            newErrors.description = 'Description is required';
        }

        if (!formData.startDate) {
            newErrors.startDate = 'Start date is required';
        }

        if (!formData.dueDate) {
            newErrors.dueDate = 'Due date is required';
        } else if (formData.startDate && new Date(formData.dueDate) < new Date(formData.startDate)) {
            newErrors.dueDate = 'Due date must be after start date';
        }

        if (formData.assignedTo.length === 0) {
            newErrors.assignedTo = 'Please assign to at least one team member';
        }

        return newErrors;
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const newErrors = validateForm();

        if (Object.keys(newErrors).length === 0) {
            try {
                const taskData = {
                    title: formData.title,
                    description: formData.description,
                    startDate: formData.startDate,
                    dueDate: formData.dueDate,
                    assigned_to: formData.assignedTo
                };

                const response = await fetch('https://plaaw9qjn7.execute-api.eu-west-1.amazonaws.com/createTask', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify(taskData)
                });

                if (!response.ok) {
                    throw new Error('Failed to submit task');
                }

                const result = await response.json();
                console.log('Task created:', result);

                setFormData({
                    title: '',
                    description: '',
                    startDate: '',
                    dueDate: '',
                    assignedTo: []
                });
                setErrors({});
                onClose();

            } catch (error) {
                console.error('Error submitting form:', error);
                setFetchError(error.message);
            }
        } else {
            setErrors(newErrors);
        }
    };

    if (loading) {
        return <p className="text-center text-gray-500">Loading team members...</p>;
    }

    if (fetchError) {
        return <p className="text-red-500 text-center">{fetchError}</p>;
    }

    return (
        <div className={`fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 ${isOpen ? 'block' : 'hidden'}`} role="dialog" aria-labelledby="dialog-title">
            <div className="bg-white rounded-lg shadow-lg p-6 w-full max-w-lg">
                <div className="dialog-header flex justify-between items-center mb-4">
                    <h3 id="dialog-title" className="text-xl font-semibold">Create New Task</h3>
                    <button onClick={onClose} className="text-gray-600 hover:text-gray-900">X</button>
                </div>

                <form onSubmit={handleSubmit} className="space-y-6">
                    <div className="space-y-2">
                        <label htmlFor="title" className="font-medium">Title</label>
                        <input
                            type="text"
                            id="title"
                            name="title"
                            value={formData.title}
                            onChange={handleInputChange}
                            className={`w-full p-2 border rounded ${errors.title ? 'border-red-500' : 'border-gray-300'}`}
                        />
                        {errors.title && <p className="text-sm text-red-500">{errors.title}</p>}
                    </div>

                    <div className="space-y-2">
                        <label htmlFor="description" className="font-medium">Description</label>
                        <textarea
                            id="description"
                            name="description"
                            value={formData.description}
                            onChange={handleInputChange}
                            className={`w-full p-2 border rounded ${errors.description ? 'border-red-500' : 'border-gray-300'}`}
                        />
                        {errors.description && <p className="text-sm text-red-500">{errors.description}</p>}
                    </div>

                    <div className="grid grid-cols-2 gap-4">
                        <div className="space-y-2">
                            <label htmlFor="startDate" className="font-medium">Start Date</label>
                            <input
                                type="date"
                                id="startDate"
                                name="startDate"
                                value={formData.startDate}
                                onChange={handleInputChange}
                                className={`w-full p-2 border rounded ${errors.startDate ? 'border-red-500' : 'border-gray-300'}`}
                            />
                            {errors.startDate && <p className="text-sm text-red-500">{errors.startDate}</p>}
                        </div>

                        <div className="space-y-2">
                            <label htmlFor="dueDate" className="font-medium">Due Date</label>
                            <input
                                type="date"
                                id="dueDate"
                                name="dueDate"
                                value={formData.dueDate}
                                onChange={handleInputChange}
                                className={`w-full p-2 border rounded ${errors.dueDate ? 'border-red-500' : 'border-gray-300'}`}
                            />
                            {errors.dueDate && <p className="text-sm text-red-500">{errors.dueDate}</p>}
                        </div>
                    </div>

                    <div className="space-y-2">
                        <label className="font-medium">Assign To</label>
                        <div className="space-y-2">
                            {teamMembers.map(member => (
                                <div key={member.id} className="flex items-center space-x-2">
                                    <input
                                        type="checkbox"
                                        id={`member-${member.id}`}
                                        checked={formData.assignedTo.includes(member.id)}
                                        onChange={() => handleCheckboxChange(member.id)}
                                    />
                                    <label htmlFor={`member-${member.id}`} className="cursor-pointer text-sm">
                                        {`${member.username} (${member.email})`}
                                    </label>
                                </div>
                            ))}
                        </div>
                        {errors.assignedTo && <p className="text-sm text-red-500">{errors.assignedTo}</p>}
                    </div>

                    <div className="flex justify-end space-x-4">
                        <button type="button" onClick={onClose} className="btn-outline py-2 px-4 rounded border border-gray-300 text-gray-600 hover:bg-gray-200">Cancel</button>
                        <button type="submit" className="btn-primary py-2 px-4 rounded bg-blue-500 text-white hover:bg-blue-600">Create Task</button>
                    </div>
                </form>
            </div>
        </div>
    );
};

export default TaskCreationForm;
