import React, { useState, useEffect } from "react";
import axios from "axios";
import { useAuth } from "react-oidc-context";

function App() {
  const auth = useAuth();
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState({
    title: "",
    description: "",
    assignee: "",
    deadline: "",
  });
  const [error, setError] = useState("");

  const API_BASE_URL = "<Your API Gateway Base URL>";

  // Fetch tasks when authenticated
  useEffect(() => {
    if (auth.isAuthenticated) {
      fetchTasks();
    }
  }, [auth.isAuthenticated]);

  const fetchTasks = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/tasks`, {
        headers: { Authorization: `Bearer ${auth.user?.access_token}` },
      });
      setTasks(response.data);
    } catch (err) {
      setError("Failed to fetch tasks");
    }
  };

  const handleCreateTask = async () => {
    try {
      const response = await axios.post(
        `${API_BASE_URL}/tasks`,
        { ...newTask },
        {
          headers: { Authorization: `Bearer ${auth.user?.access_token}` },
        }
      );
      setTasks((prevTasks) => [...prevTasks, response.data]);
      setNewTask({ title: "", description: "", assignee: "", deadline: "" });
    } catch (err) {
      setError("Failed to create task");
    }
  };

  const handleUpdateTask = async (taskId, status) => {
    try {
      const response = await axios.patch(
        `${API_BASE_URL}/tasks/${taskId}`,
        { status },
        {
          headers: { Authorization: `Bearer ${auth.user?.access_token}` },
        }
      );
      setTasks((prevTasks) =>
        prevTasks.map((task) =>
          task.task_id === taskId ? { ...task, status: response.data.status } : task
        )
      );
    } catch (err) {
      setError("Failed to update task");
    }
  };

  const signOutRedirect = () => {
    const clientId = "hfjkv26lfqqnjsi6lc7kj51ih";
    const logoutUri = "<logout uri>";
    const cognitoDomain = "https://<user pool domain>";
    window.location.href = `${cognitoDomain}/logout?client_id=${clientId}&logout_uri=${encodeURIComponent(logoutUri)}`;
  };

  if (auth.isLoading) {
    return <div>Loading...</div>;
  }

  if (auth.error) {
    return <div>Encountering error... {auth.error.message}</div>;
  }

  if (auth.isAuthenticated) {
    return (
      <div>
        <h1>Task Management App</h1>
        <div>
          <pre>Hello: {auth.user?.profile.email}</pre>
          <button onClick={() => auth.removeUser()}>Sign out</button>
        </div>

        <div>
          <h2>Create Task</h2>
          <input
            type="text"
            placeholder="Title"
            value={newTask.title}
            onChange={(e) => setNewTask({ ...newTask, title: e.target.value })}
          />
          <input
            type="text"
            placeholder="Description"
            value={newTask.description}
            onChange={(e) =>
              setNewTask({ ...newTask, description: e.target.value })
            }
          />
          <input
            type="text"
            placeholder="Assignee"
            value={newTask.assignee}
            onChange={(e) => setNewTask({ ...newTask, assignee: e.target.value })}
          />
          <input
            type="date"
            placeholder="Deadline"
            value={newTask.deadline}
            onChange={(e) => setNewTask({ ...newTask, deadline: e.target.value })}
          />
          <button onClick={handleCreateTask}>Create Task</button>
        </div>

        <div>
          <h2>Task List</h2>
          {tasks.length === 0 ? (
            <p>No tasks available</p>
          ) : (
            tasks.map((task) => (
              <div key={task.task_id}>
                <h3>{task.title}</h3>
                <p>{task.description}</p>
                <p>Status: {task.status}</p>
                <p>Deadline: {task.deadline}</p>
                <button onClick={() => handleUpdateTask(task.task_id, "COMPLETED")}>
                  Mark as Completed
                </button>
              </div>
            ))
          )}
        </div>

        {error && <p style={{ color: "red" }}>{error}</p>}
      </div>
    );
  }

  return (
    <div>
      <button onClick={() => auth.signinRedirect()}>Sign in</button>
      <button onClick={() => signOutRedirect()}>Sign out</button>
    </div>
  );
}

export default App;
