import React, { useState } from "react";
import { useAuth } from "react-oidc-context";
import Dashboard from "./components/dashboard.js";

function App() {
  const auth = useAuth();

  // Cognito configuration
  const clientId = "hfjkv26lfqqnjsi6lc7kj51ih";
  const cognitoDomain = "https://eu-west-1qpsd45zzz.auth.eu-west-1.amazoncognito.com";
  const logoutUri = "<logout uri>";
  const signUpUri = `${cognitoDomain}/signup?client_id=${clientId}&response_type=code&scope=openid+profile+email&redirect_uri=${encodeURIComponent(window.location.origin)}`;

  const signOutRedirect = () => {
    window.location.href = `${cognitoDomain}/logout?client_id=${clientId}&logout_uri=${encodeURIComponent(logoutUri)}`;
  };

  if (auth.isLoading) {
    return <div>Loading...</div>;
  }

  if (auth.error) {
    return <div>Encountering error... {auth.error.message}</div>;
  }

  if (auth.isAuthenticated) {
    return (
      <div>
        <div className="bg-gray-100 p-4">
          <h1 className="text-xl font-semibold mb-4">Welcome, {auth.user?.profile.email}</h1>
          <div className="flex space-x-4 mb-6">
            <button
              onClick={() => auth.removeUser()}
              className="px-4 py-2 bg-red-500 text-white rounded-md hover:bg-red-600"
            >
              Sign Out
            </button>
          </div>
        </div>
        {/* Render Dashboard */}
        <Dashboard />
      </div>
    );
  }

  return (
    <div className="bg-gray-100 min-h-screen flex items-center justify-center">
      <div className="p-6 bg-white rounded-md shadow-md text-center">
        <h1 className="text-2xl font-semibold mb-4">Task Management App</h1>
        <div className="space-x-4">
          <button
            onClick={() => auth.signinRedirect()}
            className="px-4 py-2 bg-blue-500 text-white rounded-md hover:bg-blue-600"
          >
            Sign In
          </button>
          <button
            onClick={() => (window.location.href = signUpUri)}
            className="px-4 py-2 bg-green-500 text-white rounded-md hover:bg-green-600"
          >
            Sign Up
          </button>
        </div>
      </div>
    </div>
  );
}

export default App;
