import React, { useState, useEffect } from "react";
import axios from "axios";
import { useAuth } from "react-oidc-context";
import {
  AppBar,
  Toolbar,
  Typography,
  Button,
  Container,
  TextField,
  Grid,
  Card,
  CardContent,
  CardActions,
} from "@mui/material";

function App() {
  const auth = useAuth();
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState({
    title: "",
    description: "",
    assignee: "",
    deadline: "",
  });
  const [error, setError] = useState("");

  const API_URLS = {
    fetchTask: "https://71aos5nio2.execute-api.eu-west-1.amazonaws.com/getTask",
    createTask: "https://plaaw9qjn7.execute-api.eu-west-1.amazonaws.com/createTask",
    updateTask: "https://ee4bon70sb.execute-api.eu-west-1.amazonaws.com/updateTask",
  };

  useEffect(() => {
    if (auth.isAuthenticated) {
      fetchTasks();
    }
  }, [auth.isAuthenticated]);

  const fetchTasks = async () => {
  try {
    const response = await fetch(API_URLS.fetchTask, {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
      },
    });

    if (!response.ok) {
      throw new Error('Failed to fetch tasks');
    }

    const data = await response.json();
    setTasks(data);
  } catch (err) {
    setError("Failed to fetch tasks");
  }
};


  const handleCreateTask = async () => {
    try {
      const response = await fetch(
        API_URLS.createTask,
        {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(newTask)
        }
      );
      const newdata = await response.json()
      setTasks((prevTasks) => [...prevTasks, newdata]);
      setNewTask({ title: "", description: "", assignee: "", deadline: "" });
    } catch (err) {
      setError("Failed to create task");
    }
  };

  const handleUpdateTask = async (taskId, status) => {
    try {
      const response = await axios.patch(
        `${API_URLS.updateTask}/${taskId}`,
        { status },
        {
          headers: { Authorization: `Bearer ${auth.user?.access_token}` },
        }
      );
      setTasks((prevTasks) =>
        prevTasks.map((task) =>
          task.task_id === taskId ? { ...task, status: response.data.status } : task
        )
      );
    } catch (err) {
      setError("Failed to update task");
    }
  };

  const signOutRedirect = () => {
    const clientId = "hfjkv26lfqqnjsi6lc7kj51ih";
    const logoutUri = "<logout uri>";
    const cognitoDomain = "https://<user pool domain>";
    window.location.href = `${cognitoDomain}/logout?client_id=${clientId}&logout_uri=${encodeURIComponent(logoutUri)}`;
  };

  if (auth.isLoading) {
    return <Typography variant="h6">Loading...</Typography>;
  }

  if (auth.error) {
    return (
      <Typography variant="h6" color="error">
        Encountering error... {auth.error.message}
      </Typography>
    );
  }

  if (auth.isAuthenticated) {
    return (
      <div>
        <AppBar position="static">
          <Toolbar>
            <Typography variant="h6" sx={{ flexGrow: 1 }}>
              Task Management App
            </Typography>
            <Button color="inherit" onClick={() => auth.removeUser()}>
              Sign Out
            </Button>
          </Toolbar>
        </AppBar>
        <Container sx={{ mt: 4 }}>
          <Typography variant="h4" gutterBottom>
            Welcome, {auth.user?.profile.email}
          </Typography>
          <Grid container spacing={2}>
            <Grid item xs={12} md={6}>
              <Typography variant="h5">Create Task</Typography>
              <TextField
                fullWidth
                label="Title"
                value={newTask.title}
                onChange={(e) => setNewTask({ ...newTask, title: e.target.value })}
                margin="normal"
              />
              <TextField
                fullWidth
                label="Description"
                value={newTask.description}
                onChange={(e) =>
                  setNewTask({ ...newTask, description: e.target.value })
                }
                margin="normal"
              />
              <TextField
                fullWidth
                label="Assignee"
                value={newTask.assignee}
                onChange={(e) =>
                  setNewTask({ ...newTask, assignee: e.target.value })
                }
                margin="normal"
              />
              <TextField
                fullWidth
                type="date"
                label="Deadline"
                value={newTask.deadline}
                onChange={(e) =>
                  setNewTask({ ...newTask, deadline: e.target.value })
                }
                margin="normal"
                InputLabelProps={{ shrink: true }}
              />
              <Button
                variant="contained"
                color="primary"
                onClick={handleCreateTask}
                sx={{ mt: 2 }}
              >
                Create Task
              </Button>
            </Grid>
            <Grid item xs={12} md={6}>
              <Typography variant="h5">Tasks</Typography>
              {tasks.length === 0 ? (
                <Typography>No tasks available</Typography>
              ) : (
                tasks.map((task) => (
                  <Card key={task.task_id} sx={{ mb: 2 }}>
                    <CardContent>
                      <Typography variant="h6">{task.title}</Typography>
                      <Typography>{task.description}</Typography>
                      <Typography>Status: {task.status}</Typography>
                      <Typography>Deadline: {task.deadline}</Typography>
                    </CardContent>
                    <CardActions>
                      <Button
                        variant="contained"
                        color="success"
                        onClick={() => handleUpdateTask(task.task_id, "COMPLETED")}
                      >
                        Mark as Completed
                      </Button>
                    </CardActions>
                  </Card>
                ))
              )}
            </Grid>
          </Grid>
          {error && (
            <Typography color="error" sx={{ mt: 2 }}>
              {error}
            </Typography>
          )}
        </Container>
      </div>
    );
  }

  return (
    <div>
      <AppBar position="static">
        <Toolbar>
          <Typography variant="h6" sx={{ flexGrow: 1 }}>
            Task Management App
          </Typography>
          <Button color="inherit" onClick={() => auth.signinRedirect()}>
            Sign In
          </Button>
        </Toolbar>
      </AppBar>
      <Container sx={{ mt: 4 }}>
        <Typography variant="h5">Please sign in to manage tasks</Typography>
      </Container>
    </div>
  );
}

export default App;
