import React, { useState, useEffect } from 'react';
import { Dialog, DialogActions, DialogContent, DialogTitle, TextField, Checkbox, FormControlLabel, Button, FormGroup, FormControl, FormHelperText, CircularProgress } from '@mui/material';

const TaskCreationForm = ({ isOpen, onClose }) => {
    const [formData, setFormData] = useState({
        title: '',
        description: '',
        startDate: '',
        dueDate: '',
        assignedTo: []
    });

    const [errors, setErrors] = useState({});
    const [teamMembers, setTeamMembers] = useState([]);
    const [loading, setLoading] = useState(true);
    const [fetchError, setFetchError] = useState(null);

    // Fetch team members from the API
    const fetchTeamMembers = async () => {
        try {
            const response = await fetch('https://pwe7hvogc6.execute-api.eu-west-1.amazonaws.com/users');
            if (!response.ok) {
                throw new Error('Failed to fetch team members');
            }
            const data = await response.json();
            const members = data.filter((user) => user.role === 'member');
            setTeamMembers(members);
        } catch (error) {
            setFetchError(error.message);
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchTeamMembers();
    }, []);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: value
        }));

        // Clear error when user starts typing
        if (errors[name]) {
            setErrors(prev => ({
                ...prev,
                [name]: ''
            }));
        }
    };

    const handleCheckboxChange = (id) => {
        setFormData(prev => ({
            ...prev,
            assignedTo: prev.assignedTo.includes(id)
                ? prev.assignedTo.filter(memberId => memberId !== id)
                : [...prev.assignedTo, id]
        }));

        if (errors.assignedTo) {
            setErrors(prev => ({
                ...prev,
                assignedTo: ''
            }));
        }
    };

    const validateForm = () => {
        const newErrors = {};

        if (!formData.title.trim()) {
            newErrors.title = 'Title is required';
        }

        if (!formData.description.trim()) {
            newErrors.description = 'Description is required';
        }

        if (!formData.startDate) {
            newErrors.startDate = 'Start date is required';
        }

        if (!formData.dueDate) {
            newErrors.dueDate = 'Due date is required';
        } else if (formData.startDate && new Date(formData.dueDate) < new Date(formData.startDate)) {
            newErrors.dueDate = 'Due date must be after start date';
        }

        if (formData.assignedTo.length === 0) {
            newErrors.assignedTo = 'Please assign to at least one team member';
        }

        return newErrors;
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const newErrors = validateForm();

        if (Object.keys(newErrors).length === 0) {
            try {
                const taskData = {
                    title: formData.title,
                    description: formData.description,
                    startDate: formData.startDate,
                    dueDate: formData.dueDate,
                    assigned_to: formData.assignedTo
                };

                const response = await fetch('https://plaaw9qjn7.execute-api.eu-west-1.amazonaws.com/createTask', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify(taskData)
                });

                if (!response.ok) {
                    throw new Error('Failed to submit task');
                }

                const result = await response.json();
                console.log('Task created:', result);

                setFormData({
                    title: '',
                    description: '',
                    startDate: '',
                    dueDate: '',
                    assignedTo: []
                });
                setErrors({});
                onClose();

            } catch (error) {
                console.error('Error submitting form:', error);
                setFetchError(error.message);
            }
        } else {
            setErrors(newErrors);
        }
    };

    if (loading) {
        return <div className="flex justify-center"><CircularProgress /></div>;
    }

    if (fetchError) {
        return <p className="text-red-500 text-center">{fetchError}</p>;
    }

    return (
        <Dialog open={isOpen} onClose={onClose} maxWidth="sm" fullWidth>
            <DialogTitle>Create New Task</DialogTitle>
            <DialogContent>
                <form onSubmit={handleSubmit}>
                    <TextField
                        label="Title"
                        name="title"
                        value={formData.title}
                        onChange={handleInputChange}
                        fullWidth
                        margin="normal"
                        error={Boolean(errors.title)}
                        helperText={errors.title}
                    />
                    <TextField
                        label="Description"
                        name="description"
                        value={formData.description}
                        onChange={handleInputChange}
                        fullWidth
                        margin="normal"
                        multiline
                        rows={4}
                        error={Boolean(errors.description)}
                        helperText={errors.description}
                    />
                    <div className="grid grid-cols-2 gap-4">
                        <TextField
                            label="Start Date"
                            name="startDate"
                            type="date"
                            value={formData.startDate}
                            onChange={handleInputChange}
                            fullWidth
                            margin="normal"
                            InputLabelProps={{
                                shrink: true,
                            }}
                            error={Boolean(errors.startDate)}
                            helperText={errors.startDate}
                        />
                        <TextField
                            label="Due Date"
                            name="dueDate"
                            type="date"
                            value={formData.dueDate}
                            onChange={handleInputChange}
                            fullWidth
                            margin="normal"
                            InputLabelProps={{
                                shrink: true,
                            }}
                            error={Boolean(errors.dueDate)}
                            helperText={errors.dueDate}
                        />
                    </div>

                    <FormControl component="fieldset" error={Boolean(errors.assignedTo)} fullWidth>
                        <FormGroup>
                            <label>Assign To</label>
                            {teamMembers.map(member => (
                                <FormControlLabel
                                    key={member.id}
                                    control={
                                        <Checkbox
                                            checked={formData.assignedTo.includes(member.id)}
                                            onChange={() => handleCheckboxChange(member.id)}
                                            name={`member-${member.id}`}
                                        />
                                    }
                                    label={`${member.username} (${member.email})`}
                                />
                            ))}
                        </FormGroup>
                        {errors.assignedTo && <FormHelperText>{errors.assignedTo}</FormHelperText>}
                    </FormControl>
                </form>
            </DialogContent>
            <DialogActions>
                <Button onClick={onClose} color="secondary">
                    Cancel
                </Button>
                <Button onClick={handleSubmit} color="primary">
                    Create Task
                </Button>
            </DialogActions>
        </Dialog>
    );
};

export default TaskCreationForm;
